import React, { useState,useEffect } from 'react';
//import icon 
import icon from './icon-offices.svg';
import { OfficesCard } from './OfficesCard';
import background from './background-offices.svg';

//data array for HTTP response data
const data=[
    {
        iconURL:icon,
        title:"Singapore",
        address:"155B Telok Ayer Street", 
        city:"Singapore, 068611"
    },
    {
        iconURL:icon,
        title:"Washington DC",
        address:"1701 Rhode Island Ave NW",
        city:"Washington, DC 20036"
    }
]

export default function Offices(){
    //component state for data
    const [info,setInfo]=useState(null);

    //set state based on server response
    useEffect(()=>{
        setInfo(data)
    },[])

    const style={
        section:{
            background:`url(${background}) no-repeat cover`
        }
    }
    //if data hasn't loaded display loading message
    if(!info) return <p>Loading...</p>


    return(

        <section style={style.section} className='container' >
            <div className="container">
                <header className="px-lg-4 text-center">
                    <h2 className=" sw-title u-space-2-top text-center mb-lg-9">
                        Our Offices
                    </h2>
                </header>
            </div>  

                {info.map(office=>{
                //destructure office obj
                const {iconURL,title,address,city}=office;
                 return(
                        <OfficesCard
                            iconURL={iconURL}
                            title={title}
                            address={address}
                            city={city}
                        />
                        )          
                    }
                )}

        </section>
    )
}
