import React, { useState,useEffect } from 'react';
//import icon 
import icon from './offices-icon.png';
// import background from './background-offices.svg';

//data array for HTTP response data
const data=[
    {
        iconURL:icon,
        title:"Singapore",
        address:"155B Telok Ayer Street", 
        city:"Singapore, 068611"
    },
    {
        iconURL:icon,
        title:"Washington DC",
        address:"1701 Rhode Island Ave NW",
        city:"Washington, DC 20036"
    }
]

function OfficesCard({iconURL,title,address,city}){
    console.log(iconURL)
    return(
    <div className="row">
            <div className="col-md-4 mb-8">
                <div className="d-flex flex-column bg-white rounded sw-rounded p-5 text-center position-relative">
                <figure className="mb-4 d-none">
                    <img 
                    style={{zIndex:"6000",position:'absolute'}}
                    src={iconURL}
                    alt="office address"/>
                </figure>
                 <h3 className="sw-heading-alt sw-heading-alt-2 mb-2">{title}</h3> 
                <p className="mb-md-0">{address}</p>
                <p className="mb-md-0">{city}</p>
                </div>

            </div>
    </div>
         )


}


export default function Offices(){
    //component state for data
    const [info,setInfo]=useState(null);

    //set state based on server response
    useEffect(()=>{
        setInfo(data)
    },[])

    const style={
        section:{
            // background:background
        }
    }
    //if data hasn't loaded display loading message
    if(!info) return <p>Loading...</p>
    console.log(data)


    return(
        <section style={style.section} className="">
            <div className="container">
                <div id="hs_cos_wrapper_how-customers-using-header" className="hs_cos_wrapper hs_cos_wrapper_widget hs_cos_wrapper_type_module"  data-hs-cos-general-type="widget" data-hs-cos-type="module">
                <header className="px-lg-4 text-center">
                    <h2 className=" sw-title u-space-2-top text-center mb-lg-9">
                        Our Offices
                    </h2>
                </header>
            </div>
            </div>

            <div>
                {info.map(office=>{
                //destructure office obj
                const {iconURL,title,address,city}=office;
                 return(
                    <OfficesCard
                        iconURL={iconURL}
                        title={title}
                        address={address}
                        city={city}
                    />
                 )       
                }
                )}
                </div>
        </section>
    )
}
