import React,{useEffect,useState} from 'react';
//import components for project

const data={
    imageURL:'./hero-img.svg',

    title:"A future where AI and humans work hand in hand",

    content:[
        "At Saleswhale, we believe that the future of work is Collaborative Intelligence - where machines and AI help humans fulfil their highest potential by automating drudgery and producing for more effective results.",
        "AI assistants help to automate repetitive and mundane tasks so that us humans focus on creative, high-impact work that creates value and inspires. The result? Happier and more productive teams doing meaningful work. \n Our mission is to put an AI assistant in every company that could greatly benefit from it, and we're starting off strong addressing this need in marketing and sales teams all across the world.",
        "The future is where AI and humans work hand in hand, and we’re here to help businesses benefit from that."
    ]


}

export default function HeroContent(){
    const [imageURL,setImageURL]=useState(null);
    const [title,setTitle]=useState(null);
    const [content,setContent]=useState(null);

    useEffect(()=>{
        setImageURL(data.imageURL);
        setTitle(data.title);
        setContent(data.content);
    },[])

    function renderContent(contentArray){
        if(!contentArray) return null;
        contentArray.map(paragraph=>{
            <p className="sw-title-summary h4 mb-4 mb-sm-7">
                {paragraph}
            </p>
        })
    }

    return(
        <div className="container sw-hero sw-hero--home u-space-2-bottom">

            <figure>
                <img src={imageURL} alt='starry night' className='sw-hero-image mb-6'/>
           </figure>

             <div className="sw-hero-content u-space-1-bottom">
                <h1 className="sw-title mb-4">
                    {title}
                </h1>
                <p className="sw-title-summary h4 mb-4 mb-sm-7">
                    {content?renderContent(content):null}
                </p>
            </div>


        </div>
    )
}
